mapper.select:
select
id, role_id, menu_id, created_at, updated_at
from role_menu


        and id = #{id}


        and role_id = #{roleId}


        and menu_id = #{menuId}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from role_menu


        and id = #{id}


        and role_id = #{roleId}


        and menu_id = #{menuId}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
